mapper.select:
SELECT

FROM outbox_event
WHERE event_type = #{bo.eventType}
AND `status` IN(0,3,4)
AND retry_count < #{bo.retryCount}
AND next_retry_at < #{bo.time}
LIMIT 500

SELECT

FROM outbox_event
WHERE `status` = #{status}
AND event_id IN

  #{it.eventId}

SELECT

FROM outbox_event
WHERE event_type = #{bo.eventType}
AND `status` IN(3,4)
AND retry_count >= #{bo.retryCount}
AND next_retry_at < #{bo.time}
LIMIT 500

mapper.update:
UPDATE outbox_event
SET `status` = #{status}
WHERE `status` IN(0, 3, 4)
AND next_retry_at < #{now}
AND event_id IN

  #{it.eventId}

UPDATE outbox_event
SET `status` = #{status}, update_time = #{now}
WHERE event_id = #{eventId}
AND `status` = #{outBoxStatus}

UPDATE outbox_event
SET event_type = #{eventType}, update_time = #{now}
WHERE event_id = #{eventId}
AND `status` IN(2,3,5,7)
AND event_type = #{oldEventType}

UPDATE outbox_event
SET `status` = #{status}
WHERE `status` IN(3,4)
AND next_retry_at < #{now}
AND event_id IN

  #{it.eventId}
